import React,{ useState } from 'react';
import axios from 'axios';

import Search from 'components/Search';
import Results from "components/Results";
import Popup from "components/Popup";


const  App = () => {
  const initialState = {
    s: "",
    results: [],
    selected : {},
    error: false
  }
  const [state, setState] = useState(initialState);
  const apiUrl = 'https://www.omdbapi.com/?apikey=574b8729';


  const search = e =>{
    if(e.key === 'Enter'){

      axios(`${apiUrl}&s=${state.s}`)      
      .then( ({data}) =>{          
        let results = data.Search;
        setState(prevState =>{
          return {...prevState, results: results, error: false}
        })
      })    
      
    }
  }

  const handleInput = e =>{
    let s  = e.target.value;
    setState(prevState=>{
      return {...prevState, s:s}
    });
  }

  const openPopup = id =>{
    axios(`${apiUrl}&i=${id}`)
    .then( ({data}) =>{
       let result = data;
       setState(prevState =>{
         return {...prevState, selected:result}
       })
    })
  }

  const closePopup = ()=>{
    setState(prevState=>{
      return {...prevState, selected:{}}
    })
  }
  
  return (
    <div>
      <header>
        <h1>Movie bd</h1>
      </header>
      <main>

        <Search handleInput={handleInput} search={search} />


         <Results results={state.results} openPopup={openPopup} error={state.error} /> 
      
          

        {
          (typeof state.selected.Title !== 'undefined') ?
          <Popup selected={state.selected} closePopup={closePopup} /> :
          false
        } 
          
      </main>
      
    </div>
  )
}

export default App;
