import React from 'react'

const Popup = ({selected, closePopup}) => {
  return (
    <section className="popup">
        <div className="content">
          <h2> {selected.Title} <span> {selected.Year} </span> </h2>
          <p className="rating">Rating: {selected.imdbRating} </p>
          <div className="plot">
            <img src={selected.Poster} alt={selected.Title} />

            <div className="content">        
              <p> <strong>Country :</strong>  {selected.Country} </p>
              <br/>
              <p> <strong>Director :</strong>  {selected.Director} </p>
              <br/>
              <p> <strong>Released :</strong>  {selected.Released} </p>
              <br/>
              <p> <strong>Actors :</strong>  {selected.Actors} </p>
              <br/>
              <p> {selected.Plot} </p>
              <br/>
              <p> <strong>Runtime :</strong>  {selected.Runtime} </p>
            </div>           
           
          </div>
          <button 
          onClick={closePopup}
          className="close"
          > 
          Back 
          </button>
        </div>
    </section>
  )
}

export default Popup
