import React from 'react'; 

import Result from "components/Result";

const Results = ({ results, openPopup }) => {

  
  return (    
    
    <section className="results">
      {
       
        results.map(result=>(
          <Result 
          result={result} 
          openPopup={openPopup}
          key={result.imdbID} 
          />
        ))
      }

    </section>    
   
  )
}

export default Results;
